import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { faPlus, faFilter, faSort } from '@fortawesome/free-solid-svg-icons';
import { TodoItemComponent } from '../todo-item/todo-item.component';
import { SupabaseService, Todo } from '../../services/supabase.service';

@Component({
  selector: 'app-todo-list',
  standalone: true,
  imports: [CommonModule, FormsModule, FontAwesomeModule, TodoItemComponent],
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.scss']
})
export class TodoListComponent implements OnInit {
  faPlus = faPlus;
  faFilter = faFilter;
  faSort = faSort;

  todos: Todo[] = [];
  filteredTodos: Todo[] = [];
  newTodoTitle = '';
  newTodoDescription = '';
  newTodoPriority: 'low' | 'medium' | 'high' = 'medium';
  showAddForm = false;
  filterStatus: 'all' | 'active' | 'completed' = 'all';
  filterPriority: 'all' | 'low' | 'medium' | 'high' = 'all';
  sortBy: 'date' | 'priority' = 'date';

  constructor(private supabaseService: SupabaseService) {}

  ngOnInit(): void {
    this.supabaseService.getTodos().subscribe(todos => {
      this.todos = todos;
      this.applyFilters();
    });
  }

  async addTodo(): Promise<void> {
    if (this.newTodoTitle.trim()) {
      try {
        await this.supabaseService.addTodo(
          this.newTodoTitle.trim(),
          this.newTodoDescription.trim(),
          this.newTodoPriority
        );
        this.resetForm();
      } catch (error) {
        console.error('Error adding todo:', error);
        // You might want to show an error message to the user here
      }
    }
  }

  resetForm(): void {
    this.newTodoTitle = '';
    this.newTodoDescription = '';
    this.newTodoPriority = 'medium';
    this.showAddForm = false;
  }

  async toggleTodo(id: number): Promise<void> {
    try {
      const todo = this.todos.find(t => t.id === id);
      if (todo) {
        await this.supabaseService.toggleTodo(id, !todo.completed);
      }
    } catch (error) {
      console.error('Error toggling todo:', error);
      // You might want to show an error message to the user here
    }
  }

  async deleteTodo(id: number): Promise<void> {
    try {
      await this.supabaseService.deleteTodo(id);
    } catch (error) {
      console.error('Error deleting todo:', error);
      // You might want to show an error message to the user here
    }
  }

  async updateTodo(id: number, updates: Partial<Todo>): Promise<void> {
    try {
      await this.supabaseService.updateTodo(id, updates);
    } catch (error) {
      console.error('Error updating todo:', error);
      // You might want to show an error message to the user here
    }
  }

  applyFilters(): void {
    this.filteredTodos = this.todos
      .filter(todo => {
        if (this.filterStatus === 'active') return !todo.completed;
        if (this.filterStatus === 'completed') return todo.completed;
        return true;
      })
      .filter(todo => {
        if (this.filterPriority === 'all') return true;
        return todo.priority === this.filterPriority;
      })
      .sort((a, b) => {
        if (this.sortBy === 'priority') {
          const priorityOrder = { high: 3, medium: 2, low: 1 };
          return priorityOrder[b.priority] - priorityOrder[a.priority];
        }
        return new Date(b.created_at).getTime() - new Date(a.created_at).getTime();
      });
  }

  onFilterChange(): void {
    this.applyFilters();
  }

  onSortChange(): void {
    this.applyFilters();
  }
}
<div class="todo-list">
  <div class="todo-controls">
    <div class="filters">
      <div class="filter-group">
        <label>
          <fa-icon [icon]="faFilter"></fa-icon>
          Status
        </label>
        <select [(ngModel)]="filterStatus" (change)="applyFilters()" class="input">
          <option value="all">All</option>
          <option value="active">Active</option>
          <option value="completed">Completed</option>
        </select>
      </div>

      <div class="filter-group">
        <label>
          <fa-icon [icon]="faSort"></fa-icon>
          Sort By
        </label>
        <select [(ngModel)]="sortBy" (change)="applyFilters()" class="input">
          <option value="date">Sort by Date</option>
          <option value="priority">Sort by Priority</option>
        </select>
      </div>

      <div class="filter-group">
        <label>
          <fa-icon [icon]="faFilter"></fa-icon>
          Priority
        </label>
        <select [(ngModel)]="filterPriority" (change)="applyFilters()" class="input">
          <option value="all">All Priorities</option>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
      </div>
    </div>

    <button class="btn btn-primary" (click)="showAddForm = !showAddForm">
      <fa-icon [icon]="faPlus"></fa-icon>
      Add New Task
    </button>
  </div>

  <div *ngIf="showAddForm" class="add-todo-form animate-slide">
    <h3>Add New Task</h3>
    <div class="form-group">
      <input type="text" [(ngModel)]="newTodoTitle" class="input" placeholder="Task title">
    </div>
    <div class="form-group">
      <textarea [(ngModel)]="newTodoDescription" class="input" placeholder="Task description"></textarea>
    </div>
    <div class="form-group">
      <select [(ngModel)]="newTodoPriority" class="input">
        <option value="low">Low Priority</option>
        <option value="medium">Medium Priority</option>
        <option value="high">High Priority</option>
      </select>
    </div>
    <div class="form-actions">
      <button class="btn btn-primary" (click)="addTodo()">Add Task</button>
      <button class="btn btn-secondary" (click)="resetForm()">Cancel</button>
    </div>
  </div>

  <div class="todos-container">
    <div *ngIf="filteredTodos.length === 0" class="empty-state">
      <p>No tasks found. Add a new task to get started!</p>
    </div>

    <app-todo-item
      *ngFor="let todo of filteredTodos"
      [todo]="todo"
      (toggle)="toggleTodo($event)"
      (delete)="deleteTodo($event)"
      (update)="updateTodo($event.id, $event.updates)"
    ></app-todo-item>
  </div>
</div>
